import React, {Component} from "react";
// import reactstrap from "reactstrap";
import {Media,Card, CardImg, CardImgOverlay, CardText, CardBlock, CardTitle, CardBody} from "reactstrap";
//import { DISHES } from "../shared/dishes";

class DishDetails extends Component{
    constructor(props){
        super(props);
        this.state = {
            commen: null
        };
    }
    onDishSelect(arr){
        this.setState({commen:arr.comments})
    }
    renderDish(dish) {
        if (dish != null)
            return (
                <Card className="col-12 col-md-5 m-1">
                    <CardImg top width="100%" src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );
        else
            return (
                <div></div>
            );
    }
    renderComments(array) {
        if(array != null){
        const abc = array.comments.map((arr) => 
            <div key={arr.id} onClick={() => this.onDishSelect(array.comments)}>
            <ul className="list-unstyled">
                <li>
                    <p>{arr.comment}</p>
                    <p>-- {arr.author} , {arr.date}</p>
                </li>
            </ul>
            </div>
        )
        return (
                <div className="col-12 col-md-5 m-1">
                    <h4>Comments</h4>
                    <div>
                        {abc}   
                    </div>
                </div>
            )
        }
    else{
        return(
            <div></div>
        )
    }
}


    render(){

            let dish=(
                    <div className="row">
                        {this.renderDish(this.props.selectedDish)}
                        {this.renderComments(this.props.selectedDish)}
                    </div>
                )
        return(
            <div className="container">
            {dish}
            </div>
            );
    }

}

export default DishDetails;
