import React from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';


    function RenderDish({dish}) {

        if (dish != null)
        return (
            <Card className="col-12 col-md-5 m-1">
                <CardImg top width="100%" src={dish.image} alt={dish.name} />
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>
            </Card>
        );
        else
            return (
                <div></div>
            );
    }

    function RenderComments({array}) {

        if(array != null){
            const abc = array.comments.map((arr) => 
                <div key={arr.id} onClick={array.onClick}>
                <ul className="list-unstyled">
                    <li>
                        <p>{arr.comment}</p>
                        <p>-- {arr.author} , {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(arr.date)))}</p>
                    </li>
                </ul>
                </div>
            )
            return (
                    <div className="col-12 col-md-5 m-1">
                        <h4>Comments</h4>
                        <div>
                            {abc}   
                        </div>
                    </div>
                )
            }
        else{
            return(
                <div></div>
            )
        }

    }

    const  DishDetail = (props) => {
        let dish=(
            <div className="row">
                <RenderDish dish={props.dish} /> 
                <RenderComments array={props.dish}/>
            </div>
        )
        return(
            <div className="container">
            <div className="row">
                <Breadcrumb>

                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{props.dish.name}</h3>
                    <hr />
                </div>                
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderDish dish={props.dish} />
                </div>
                <div className="col-12 col-md-5 m-1">
                    <RenderComments comments={props.comments} />
                </div>
            </div>
            </div>
            );
        }

export default DishDetail;
