            return( 
                <div key={dish.Comments.id} className="col-12 mt-5">
                    <div className="row"><Media heading>Comments</Media></div>
                    <Media tag="li">
                      <Media body className="ml-5">
                        <p>{dish.Comments.comment}</p>
                        <p>-- {dish.comments.author} {dish.comments.comment}</p>
                      </Media>
                    </Media>
                  </div>

                );
            }
        )
        return(
                <div className="row">
                        <Card>
                            <CardImg width="100%" object src={this.props.image} alt={this.props.name} />
                            <CardBody>
                                <CardTitle>{this.props.name}</CardTitle>
                                <CardText>{this.props.description}</CardText>
                            </CardBody>
                        </Card>
                    {/* <div className="col-12 col-md-5 m-1">
